// frontend/src/pages/dashboard/SubirTrabajo.js
import React, { useState } from 'react';
import axios from 'axios';
import { api } from '../../config/api'; // <-- IMPORTAR LA INSTANCIA DE AXIOS PRE-CONFIGURADA
import { useNavigate } from 'react-router-dom';

export default function SubirTrabajo() {
  const [titulo, setTitulo] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [tipo, setTipo] = useState('especial_grado');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const navigate = useNavigate();

  const handleFile = (e) => {
    const f = e.target.files && e.target.files[0];
    setFile(f || null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    if (!titulo || !file) {
      setError({ detail: 'Título y archivo son obligatorios' });
      return;
    }

    const form = new FormData();
    form.append('titulo', titulo);
    form.append('descripcion', descripcion);
    form.append('tipo', tipo);
    form.append('archivo', file);

    try {
      setLoading(true);
      const res = await api.post('/trabajos/', form, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setSuccess(res.data || { detail: 'Subido correctamente' });
      // Optionally redirect to trabajos list or detail
      setTimeout(() => navigate('/trabajos'), 900);
    } catch (err) {
      setError(err.response?.data || { detail: err.message });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: 720 }}>
        <h1>Subir Trabajo</h1>
        {error && <div style={{ color: 'red', marginBottom: 8 }}>Error: {JSON.stringify(error)}</div>}
        {success && <div style={{ color: 'green', marginBottom: 8 }}>Éxito: {JSON.stringify(success)}</div>}

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: 8 }}>
            <label>Título</label>
            <input required value={titulo} onChange={(e) => setTitulo(e.target.value)} style={{ width: '100%', padding: 8, marginTop: 4 }} />
          </div>

          <div style={{ marginBottom: 8 }}>
            <label>Descripción</label>
            <textarea value={descripcion} onChange={(e) => setDescripcion(e.target.value)} rows={4} style={{ width: '100%', padding: 8, marginTop: 4 }} />
          </div>

          <div style={{ marginBottom: 8 }}>
            <label>Tipo</label>
            <select value={tipo} onChange={(e) => setTipo(e.target.value)} style={{ width: '100%', padding: 8, marginTop: 4 }}>
              <option value="especial_grado">Especial de grado</option>
              <option value="practicas_profesionales">Prácticas profesionales</option>
            </select>
          </div>

          <div style={{ marginBottom: 8 }}>
            <label>Archivo</label>
            <input required type="file" onChange={handleFile} style={{ display: 'block', marginTop: 4 }} />
          </div>

          <div style={{ display: 'flex', gap: 8, justifyContent: 'flex-end' }}>
            <button type="button" onClick={() => navigate('/trabajos')} className="btn">Cancelar</button>
            <button type="submit" disabled={loading} className="btn">{loading ? 'Subiendo...' : 'Subir'}</button>
          </div>
        </form>
      </div>
    </div>
  );
}
