// frontend/src/config/api.js

// URL base del backend de Django (el contenedor expone el puerto 8001)
export const API_BASE_URL = 'http://127.0.0.1:8001/api/v1';

// Puedes exportar una instancia de axios pre-configurada aquí también
import axios from 'axios';

export const api = axios.create({
    baseURL: API_BASE_URL,
    // Configuración para que axios envíe cookies/headers de autenticación si son necesarios
    withCredentials: true, 
});
