import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function ListaTrabajos() {
  const [trabajos, setTrabajos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;
    setLoading(true);
    axios
      .get('/api/v1/trabajos/')
      .then((res) => {
        if (!mounted) return;
        setTrabajos(res.data || []);
        setError(null);
      })
      .catch((err) => {
        if (!mounted) return;
        setError(err.response?.data || { detail: err.message });
      })
      .finally(() => {
        if (!mounted) return;
        setLoading(false);
      });

    return () => {
      mounted = false;
    };
  }, []);

  return (
    <div className="container">
      <div className="card">
        <h1>Lista de trabajos</h1>

        {loading && <p>Cargando trabajos...</p>}

        {error && (
          <div style={{ color: 'red' }}>
            Error cargando trabajos: {JSON.stringify(error)}
          </div>
        )}

        {!loading && !error && (
          <div>
            {trabajos.length === 0 ? (
              <p>No hay trabajos disponibles.</p>
            ) : (
              <ul>
                {trabajos.map((t) => (
                  <li key={t.id} style={{ marginBottom: 8 }}>
                    <strong>{t.titulo || t.nombre || `Trabajo ${t.id}`}</strong>
                    <div style={{ fontSize: 12, color: '#444' }}>
                      {t.descripcion || t.resumen || ''}
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
